import React from "react";
import "./Homepage.css";
import { useNavigate } from "react-router-dom";
import Navbar from "../../components/Navbar/Navbar";
import "./Homepage.css";
import { useState, useEffect } from "react";
import axios from "axios";

class ChannelsPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      news: [],
      loading: true,
    };
  }

  componentDidMount() {
    // Fetch sports news data from the News API
    const url =
      "https://newsapi.org/v2/everything?" +
      "q=Sports&" +
      "from=2023-07-01&" +
      "sortBy=popularity&" +
      "apiKey=525463574a92443c9f37de100b90cbd5";

    fetch(url)
      .then((response) => response.json())
      .then((data) => this.setState({ news: data.articles, loading: false }))
      .catch((error) => console.log(error));
  }

  render() {
    const { news, loading } = this.state;

    return (
      <div>
        <h1>Sports News</h1>
        {loading ? (
          <p>Loading news...</p>
        ) : (
          <ul>
            {news.map((article, index) => (
              <li key={index}>
                <h3>{article.title}</h3>
                <p>{article.description}</p>
                <p>Published on: {article.publishedAt}</p>
              </li>
            ))}
          </ul>
        )}
      </div>
    );
  }
}

export default ChannelsPage;
