import React from 'react';
import './Homepage.css';
import { Swiper, SwiperSlide } from 'swiper/react';
import profilePic1 from '../../img/1.jpg';
import profilePic2 from '../../img/2.jpg';
import profilePic3 from '../../img/3.jpg';
import profilePic4 from '../../img/1.jpg';
import { Pagination } from 'swiper';
import 'swiper/css/pagination';
import 'swiper/css';
import Weather from '../../components/Weather/Weather';
import { useNavigate } from 'react-router-dom';
import Navbar from '../../components/Navbar/Navbar';

import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import { FaPlay, FaTv, FaClock, FaCog } from 'react-icons/fa';

function HomePage() {
  let navigate = useNavigate();
  const clients = [
    {
      img: profilePic1,
      name: 'Recommend crops for current seasons and locations',
    },
    {
      img: profilePic2,
      name: 'Consider Weather and Typical Season',
    },
    {
      img: profilePic3,
      name: 'Helps with planting decisions',
    },
    {
      img: profilePic4,
      name: 'Improves yield and success',
    },
    {
      img: profilePic4,
      name: 'Timing is Important for maximum Harvest',
    },
  ];

  const videos = [
    'https://www.youtube.com/embed/Vhtmu2osUmE',
    'https://www.youtube.com/embed/N5lkJ6aBWe4',
    'https://www.youtube.com/embed/bL7VReeCWqI',
    'https://www.youtube.com/embed/bL7VReeCWqI',
    'https://www.youtube.com/embed/bL7VReeCWqI',
    'https://www.youtube.com/embed/bL7VReeCWqI',
    // Add more video URLs here
  ];

  const handleClick = () => {
    const isLoggedIn = localStorage.getItem('isLoggedIn') === 'true';

    if (isLoggedIn) {
      navigate('/prediction');
    } else {
      toast.error('Please login or sign up first', {
        autoClose: 1000,
      });
    }
  };
  return (
    <>
      <Navbar />

      <div className="t-wrapper" id="SeasonalPicks">
        <div className="left">
          <div>
            <ul id="sideBar">
              <li>
                <a href="/" className="active">
                  <i
                    className="fas fa-play fa-xs"
                    style={{ color: 'white' }}
                  ></i>
                  Live
                </a>
              </li>
              <li>
                <i className="fas fa-tv fa-xs" style={{ color: 'white' }}></i>
                <a href="index.html">Channel</a>
              </li>
              <li>
                <i
                  className="fas fa-clock fa-xs"
                  style={{ color: 'white' }}
                ></i>
                <a href="index.html">Latest</a>
              </li>
              <li>
                <i className="fas fa-cog fa-xs" style={{ color: 'white' }}></i>
                <a href="index.html">Setting</a>
              </li>
            </ul>
          </div>
        </div>

        <div className="right">
          {/* Embed YouTube videos */}
          <div className="video-wrapper">
            {videos.map((video, index) => (
              <iframe
                key={index}
                width="585"
                height="315"
                src={video}
                title={`YouTube video player ${index}`}
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
                allowFullScreen
              ></iframe>
            ))}
          </div>
        </div>
      </div>
    </>
  );
}

export default HomePage;
